import { useLocation, Link } from "react-router";
import { useState } from "react";
import Search from "./header/Search";
import Menu from "./header/Menu";
import Notification from "./header/Notification";

export default function Header() {
  const location = useLocation();

  const [openSearch, setOpenSearch] = useState(false);
  const [openNotification, setOpenNotification] = useState(false);
  const [openMenu, setOpenMenu] = useState(false);

  return (
    <>
      <header className="py-4.5 px-4 z-99">
        <nav className="flex justify-between w-full">
          <Link to="/">
            <div className="logo pointer-cursor">
              {location.pathname === "/" ? logo : arrow}
            </div>
          </Link>
          <div className="flex items-center gap-4 nav-items">
            <button
              className="cursor-pointer"
              onClick={() => setOpenSearch((prev) => !prev)}
            >
              {search}
            </button>
            <button
              className="cursor-pointer"
              onClick={() => setOpenNotification((prev) => !prev)}
            >
              {notification}
            </button>
            <button
              className="cursor-pointer"
              onClick={() => setOpenMenu((prev) => !prev)}
            >
              {burger}
            </button>
          </div>
        </nav>
      </header>

      {openSearch && <Search closeSearch={() => setOpenSearch(false)} />}
      {openNotification && (
        <Notification closeNotification={() => setOpenNotification(false)} />
      )}
      {openMenu && (
        <Menu closeMenu={() => setOpenMenu(false)} isOpen={openMenu} />
      )}
    </>
  );
}

